import classes from "./Checkout.module.css";
import Modal from "../UI/Modal";
import useInput from "../../hooks/use-input";

const isNotEmpty = (value) => value.trim() !== "";
const Checkout = (props) => {
  const {
    value: enteredName,
    hasError: nameHasError,
    reset: resetNameInput,
    valueIsValid: nameIsValid,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
  } = useInput(isNotEmpty);
  const {
    value: enteredAddress,
    hasError: addressHasError,
    reset: resetAddressInput,
    valueIsValid: addressIsValid,
    valueChangeHandler: addressChangeHandler,
    inputBlurHandler: addressBlurHandler,
  } = useInput(isNotEmpty);
  const {
    value: enteredCity,
    hasError: cityHasError,
    reset: resetCityInput,
    valueIsValid: cityIsValid,
    valueChangeHandler: cityChangeHandler,
    inputBlurHandler: cityBlurHandler,
  } = useInput(isNotEmpty);
  const {
    value: enteredZip,
    hasError: zipHasError,
    reset: resetZipInput,
    valueIsValid: zipIsValid,
    valueChangeHandler: zipChangeHandler,
    inputBlurHandler: zipBlurHandler,
  } = useInput(isNotEmpty);

  const orderConfirmedHandler = () => {
    props.onClose();
    <Modal onClose={props.onClose}>
      <section>
        <p>
          Thank you for your order! Your order will be arriving in 30 minutes.
        </p>
      </section>
    </Modal>;
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    props.onClose();
    <Modal>
      <section>
        <p>
          Thank you for your order! Your order will be arriving in 30 minutes.
        </p>
      </section>
    </Modal>;
    // if (!firstNameIsValid || !lastNameIsValid || !emailIsValid) {
    //   return;
    // }
    // console.log(enteredFirstName, enteredLastName, enteredEmail);
    // resetFirstNameInput();
    // resetLastNameInput();
    // resetEmailInput();
    if (!nameIsValid || !addressIsValid || !zipIsValid || !cityIsValid) {
      return;
    }
    resetAddressInput();
    resetZipInput();
    resetCityInput();
    resetNameInput();
  };

  const nameInputClasses = !nameHasError ? "control" : "invalid";
  const addressInputClasses = !addressHasError ? "control" : "invalid";
  const cityInputClasses = !cityHasError ? "control" : "invalid";
  const zipInputClasses = !zipHasError ? "control" : "invalid";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control">
        <div className={nameInputClasses}>
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
            value={enteredName}
          ></input>
          {nameHasError && <p className="invalid">Name is empty.</p>}
        </div>
        <div className={addressInputClasses}>
          <label htmlFor="address">Address</label>
          <input
            type="text"
            id="address"
            onChange={addressChangeHandler}
            onBlur={addressBlurHandler}
            value={enteredAddress}
          ></input>
          {addressHasError && <p className="error-text">Address is empty.</p>}
        </div>
        <div className={cityInputClasses}>
          <label htmlFor="city">City</label>
          <input
            type="text"
            id="city"
            onChange={cityChangeHandler}
            onBlur={cityBlurHandler}
            value={enteredCity}
          ></input>
          {cityHasError && <p className="error-text">City is empty.</p>}
        </div>
        <div className={zipInputClasses}>
          <label htmlFor="zip">ZIP Code</label>
          <input
            type="text"
            id="zip"
            onChange={zipChangeHandler}
            onBlur={zipBlurHandler}
            value={enteredZip}
          ></input>
          {zipHasError && <p className="error-text">ZIP Code is empty.</p>}
        </div>
      </div>
      <div className={classes.actions}>
        <button onClick={props.onClose}>Cancel</button>
        <button className={classes["submit"]}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
